    if (firstRenderRef.current) {
      firstRenderRef.current = false;
      return;
    }

    // \u5916\u90E8value\u7B49\u4E8Eundefined\uFF0C\u4E5F\u5C31\u662F\u4E00\u5F00\u59CB\u6709\u503C\uFF0C\u540E\u6765\u53D8\u6210\u4E86undefined\uFF08
